mapper.select:
SELECT n_idx, n_category, n_title, DATE_FORMAT(n_regdate, '%Y.%m.%d') as n_regdate FROM tb_notice WHERE n_status = 'N'
AND n_category = #{category}
AND (n_title LIKE CONCAT('%', #{searchKeyword}, '%') OR n_content LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY n_idx DESC LIMIT #{start}, #{limit}

SELECT COUNT(*) FROM tb_notice WHERE n_status = 'N'
AND n_category = #{category}
AND (n_title LIKE CONCAT('%', #{searchKeyword}, '%') OR n_content LIKE CONCAT('%', #{searchKeyword}, '%'))
